<template>
		<view class="wrap">
			<swiper class="swiper-box" :duration="'200'" :current="swiperCurrent" @transition="transition" @animationfinish="animationfinish" @change="tabListSwitch">
				<swiper-item class="swiper-item" v-for="(item, index) in tabList" :key="index">
					<!-- <scroll-view scroll-y style="height: 100%;width: 100%;" @scrolltolower="reachBottom"> -->
					<scroll-view scroll-y style="height: 100%;width: 100%;" :scroll-top="scrollTop" scroll-y="true" @scroll="scroll" @scrolltolower="loadMore">
						<xz-item :item="item" :collected="collectIds.includes(item.id)" v-for="item in tabContent[index]" :key="item.id"></xz-item>
						<u-loadmore :status="uloadmore.status" :loadText="uloadmore.loadText"
						:icon-type="uloadmore.iconType" :is-dot="uloadmore.isDot" @loadmore="loadMore" margin-top="20"/>
					</scroll-view>
				</swiper-item>
			</swiper>
			<view class="u-tabs-box">
				<u-tabs-swiper :activeColor="platformColor.fore" ref="tabs" 
				:list="tabList" :current="current" @change="tabSwitch" 
				:bold="false" :barWidth="60" :barStyle="barStyle"></u-tabs-swiper>
			</view>
			<u-toast ref="uToast"></u-toast>
			<u-loading mode="circle" :show="loadingShow" :color="platformColor.back" size="60" class="loading" ></u-loading>
			<view  @click="goTop">
				<c-back-top :mode="backTop.mode" :scrollTop="backTop.scrollTop"
				:bottom="backTop.bottom" :right="backTop.right" :top="backTop.top"
				:icon-style="backTop.iconStyle" :customStyle="backTop.customStyle" 
				:icon="backTop.icon">
				</c-back-top>
			</view>
		</view>

</template>

<script>
	import xzItem from "@/components/xianzhi/xz-item.vue"
	export default {
		data() {
			return {
				platform: "xianzhi",
				collectIds: [],
				tabList: [],
				tabContent: [],
				current: 0,
				swiperCurrent: 0,
				dx: 0,
				platformColor: {
					back: "#ebebeb",
					fore: "#373D41"
				},
				loadingShow: false,
				scrollTop: 0,
				old: {
					scrollTop: 0
				},
				backTop: {
					top: 600,
					scrollTop: 0,
					mode: "circle",
					bottom: 200,
					right: 30,
					customStyle: {
						backgroundColor: "#ebebeb"
					},
					icon: "arrow-up",
					iconStyle: {
						color: '#373D41',
						fontSize: '40rpx'
					},
					duration: 300
				},
				activeBytab: false,
				barStyle: {
					marginBottom: "60rpx"
				},
				uloadmore: {
					status: 'loading',
					iconType: 'flower',
					isDot: true,
					loadText: {
						loadmore: 'click / slide',
						loading: 'loading...',
						nomore: 'no more'
					}
				},
				currentPage: 1,
				maxPage: 5,
				baseAvatar: 'data:image/png;base64,'
			};
		},
		components: {
			xzItem
		},
		created() {
			this.current = 0,
			this.swiperCurrent = 0
			this.getTabs()
		},
		methods: {
			async getTabs() {
				this.loadingShow = true
				const {
					data
				} = await this.$surf.getTabs({
					platform: this.platform
				})
				if (data) {
					this.tabList = data
					this.getTabContent(0)
				}
				this.loadingShow = false
			},
			async getTabContent(tabIndex) {
				if (this.tabContent[tabIndex]) return
				let self = this
				self.loadingShow = true
				self.$surf.getTabContent({
					name: self.tabList[tabIndex].name, 
					platform: self.platform,
					}).then(res => {
						self.loadingShow = false
						if (res.data[0].content.length > 0) {
							self.$set(self.tabContent, tabIndex, res.data[0].content);
						}else {
							self.getLatestTabContent(true, tabIndex)
						}	
						setTimeout(function() {
							self.uloadmore.status = "loadmore"
							self = null
						}, 500)
					})
			},
			async getLatestTabContent(firstView, tabIndex) {
				console.log("latest")
				let self = this
				uni.showLoading({
					title: 'Conn XianZhi...'
				})
				uni.request({
					url: self.tabList[tabIndex].link,
					method: 'GET',
					data: {},
					success: res => {
						if (res.statusCode === 200) {
							var feedBack = res.data
							var xz_content = feedBack.match(/<td>([\s\S]*?)<\/td>/g)
							if (!xz_content) {
								self.uloadmore.status = "nomore"
								uni.showToast({
									duration: 2000,
									title: "No match",
									icon: "none"
								})
								return
							}
							if ((!firstView) && (xz_content[0].match(/<a\sclass="topic-title"\s*href="\/t\/([0-9]*?)">[\s\S]*?<\/a>/)[1] === self.tabContent[self.current][0].id)) {
								self.$refs.uToast.show({
									title: "Nothing new",
									position: "top",
									type: "default",
									duration: 2000
								});
								return
							}
							self.$refs.uToast.show({
								title: "New data",
								position: "top",
								type: "default",
								duration: 2500,
								bgColor: self.platformColor.back,
								ttColor: self.platformColor.fore
							});
							self.parseItem(xz_content, "set")
							self.goTop()
						}else {
							self.uloadmore.status = "nomore"
							self.$refs.uToast.show({
								title: "link changed",
								position: "top",
								type: "error",
								duration: 2500,
								bgColor: self.platformColor.back,
								ttColor: self.platformColor.fore
							})
						}

					},
					fail: err => {
						self.uloadmore.status = "nomore"
						uni.showToast({
							title: "Network Blocked",
							icon: "none"
						})
					},
					complete() {
						uni.hideLoading()
						self = null
					}
				})
			},
			async getMoreContent() {
				let self = this
				self.uloadmore.status = "loading"
				uni.request({
					url: "https://xz.aliyun.com/?page=" + (self.currentPage + 1),
					method: 'GET',
					data: {},
					success: res => {
						var feedBack = res.data
						var xz_content = feedBack.match(/<td>([\s\S]*?)<\/td>/g)
						if (!xz_content) {
							uni.showToast({
								duration: 2000,
								title: "No Match",
								icon: "none"
							})
							return
						}
						self.parseItem(xz_content, "push")
					},
					fail: res => {
						self.uloadmore.status = "loadmore"
						uni.showToast({
							title: "Network Blocked",
							icon: "none",
							duration: 2000
						})
					},
					complete() {
						self = null
					}
				})
			},
			parseItem(xz_content, purpose) {
				let filterContent = []
				let tempContent = {}
				let author = {}
				let article = {}
				let tag = {}
				for (var i = 0, len = xz_content.length; i != len; ++i) {
					author = xz_content[i].match(/<a\s*href="\/u\/([0-9]*?)">([\s\S]*?)<\/a>/),
					article = xz_content[i].match(/<a\sclass="topic-title"\s*href="\/t\/([0-9]*?)">([\s\S]*?)<\/a>/),
					tag = xz_content[i].match(/<a\s*href="\/node\/([0-9]*?)">([\s\S]*?)<\/a>/),
					tempContent = {
						id: article[1] || "110119",
						cover: xz_content[i].match(/<img\s*class="avatar\s*tiny-avatar"\s*src="([\s\S]*?)"/)[1] || this.baseAvatar,
						title: article[2] ? article[2].trim() : "No Title",
						tag: {
							name: tag[2] || "安全分析",
							id: tag[1] || "110119"
						},
						date: xz_content[i].match(/\/\s*([0-9,\-]*)\s*<span/)[1] || "2020-08-05 21:40:39",
						author: {
							id: author[1] || "110119",
							name: author[2] || "no name"
						},
						comment_count: xz_content[i].match(/<span\s*class="badge\s*badge-hollow\s*text-center\s*?">([0-9]*?)<\/span>/)[1] || 0
					}
					filterContent[i] = tempContent
				}
				if (purpose === "set") {
					this.$set(this.tabContent, this.current, filterContent);
					this.newTabContent()
				}else if (purpose === "push") {
					this.tabContent[this.current].push(...filterContent)
				}
				let self = this
				setTimeout(function() {
					if (++self.currentPage >= self.maxPage) {
						self.uloadmore.status = "nomore"
					}else {
						self.uloadmore.status = "loadmore"
					}
					self = null
				}, 500)
				filterContent = []
				tempContent = {}
				author = {}
				article= {}
				tag = {}
			},
			async newTabContent() {
				this.$surf.newTabContent({_id: this.tabList[this.current]._id, platform: this.platform, tabContent: this.tabContent[this.current]})
			},
			tabSwitch(current) {
				this.activeBytab = true
				this.swiperCurrent = current
				this.getTabContent(current);
			},
			tabListSwitch(e) {
				if (this.activeBytab) {
					this.activeBytab = false
					return
				}
				const {
					current
				} = e.detail
				this.getTabContent(current);
			},
			transition({
				detail: {
					dx
				}
			}) {
				this.$refs.tabs.setDx(dx)
			},
			animationfinish({
				detail: {
					current
				}
			}) {
				this.$refs.tabs.setFinishCurrent(current)
				this.swiperCurrent = current
				this.current = current
			},
			injectPs(target) {
				if (target === "new") {
					this.getLatestTabContent(false, this.current)
				}else if (target === "cids"){
					this.getCollectIds()
				}
			},
			async getCollectIds() {
				let self = this
				uni.getStorage({
				    key: 'xianzhi-collect',
				    success: function (res) {
						self.collectIds = res.data
				    },
					fail() {
						self.$surf.getCollectIds({
							platform: "xianzhi",
						}).then(res => {
							self.collectIds = res.data[0].collect_ids
						})
					}
				});
			
			},
			scroll (e) {
				this.old.scrollTop = e.detail.scrollTop
				this.backTop.scrollTop =  e.detail.scrollTop
			},
			goTop() {
				this.scrollTop = this.old.scrollTop
				this.$nextTick(function() {
					this.scrollTop = 0
				});
			},
			loadMore() {
				if (this.currentPage >= this.maxPage || this.uloadmore.status === "loading") return
				this.getMoreContent()
			}
		}
	};
</script>

<style lang="scss">
	page {
		height: 100%;
		display: flex;
	}
	
	.wrap {
		display: flex;
		flex-direction: column;
		height: 100%;
		width: 100%;
		
		.swiper-box {
			height: 100%;
			overflow: hidden;
			.swiper-item {
				height: 100%;
			}
		}
	}


	.loading {
		position: fixed;
		left: 45%;
		top: 45%;
	}
</style>
